  title: String,
  createdAt: Date
});
const BlogsModel = mongoose.model('Blogs', BlogsModelSchema);
new BlogsModel()

// blogs
var QuestionsModelSchema = new Schema({
  question: String,
  question_type: String,
  options: Object,
  created_at: Date
});
const QuestionsModel = mongoose.model('Questions', QuestionsModelSchema);
new QuestionsModel()


// photos
var PhotosModelSchema = new Schema({
  title: String,
  file: Array,
  created_at: Date
});
const PhotosModel = mongoose.model('Photos', PhotosModelSchema);
new QuestionsModel()

// users
var UsersModelSchema = new Schema({
  username: String,
  nickname: String,
  telphone: String,
  email: String,
  password: String,
  updated_at: Date,
  last_login_at: Date,
  created_at: Date
});
